// Export function
export function getB1CurriculumExercisesBySkillId(skillId: string): Exercise[] {
  const allExercises: Record<string, Exercise[]> = {
    ...b1VocabExercises,
    ...b1GrammarExercises,
    ...b1SpeakingExercises,
    ...b1ListeningExercises,
    ...b1ReadingExercises,
    ...b1WritingExercises,
    ...b1StrategiesExercises,
  };
  
  return allExercises[skillId] || [];
}

