// Utility functions
export const getVocabularyByLevel = (level: "A1" | "A2" | "B1" | "B2" | "C1" | "C2"): VocabularyCategory[] => {
  return expandedVocabularyCategories.filter(cat => cat.level === level);
};

export const getTotalWordCount = (): number => {
  return expandedVocabularyCategories.reduce((total, cat) => total + cat.words.length, 0);
};

export const getCategoryCount = (): number => {
  return expandedVocabularyCategories.length;
};

export const getWordsByLevel = (level: "A1" | "A2" | "B1" | "B2" | "C1" | "C2"): Word[] => {
  const categories = getVocabularyByLevel(level);
  return categories.flatMap(cat => cat.words);
};
